/// <summary>
/// Sum two integers
/// </summary>
/// <param name = "_x">First value to be summed</param>
/// <param name = "_y">Second value to be summed</param>
/// <returns>The sum of x and y</returns>
public int SumIntegers(int _x, int _y)
{
    return _x + _y;
}
